import React, { MouseEventHandler } from 'react'
import styled from 'styled-components'

const Button = styled.button`
  padding: 0px;
  background-color: transparent;
  border: none;
  width: 48px;
  height: 48px;
  border-radius: 50%;
  cursor: pointer;
  .icon-outer {
    fill: #8c69ff;
  }

  &:disabled {
    opacity: 20%;
    cursor: default;
  }

  &:focus {
    border: 2px solid #d9cdff;
    border-radius: 10px;
  }

  &:hover {
    border: none;
    .icon-outer {
      fill: #7f5cf4;
    }
  }

  &:active {
    .icon-inner {
      fill: #d9cdff;
    }
  }
`

