const WideButton = styled.button`
  width: 116px;
  height: 48px;
  padding: 0px;
  background-color: transparent;
  border: none;
  cursor: pointer;

  .icon-outer {
    fill: #ffffff;
  }

  .icon-inner {
    fill: #1a1a1a;
  }

  &:disabled {
    cursor: default;
    opacity: 20%;
  }

  &:focus {
    .icon-border {
      stroke: #1a1a1a;
      color: #ffffff99;
    }
  }

  &:hover {
    .icon-outer {
      fill: #1a1a1a;
    }

    .icon-inner {
      fill: #ffffff;
    }

    .icon-border {
      stroke: #1a1a1a;
    }
  }

  &:active {
    .icon-inner {
      fill: #1a1a1a;
    }
  }
`

